mapper.select:
SELECT COUNT(*) FROM tbl_item I INNER JOIN tbl_correspondent_item CI ON CI.ITEM_CODE = I.ITEM_CODE INNER JOIN tbl_correspondent C ON CI.CORRESPONDENT_CODE = C.CORRESPONDENT_CODE INNER JOIN tbl_sub_category SC ON SC.SUB_CATEGORY_CODE = I.CATEGORY_CODE WHERE I.ITEM_CODE IS NOT NULL
AND I.NAME LIKE CONCAT('%', #{itemName}, '%')
AND I.ITEM_UNIQUE_CODE LIKE CONCAT('%', #{itemCode}, '%')
AND SC.SUB_CATEGORY_CODE = ${categoryCode}
AND C.CORRESPONDENT_CODE = #{correspondentCode}
