mapper.select:
SELECT P.LETTER_OF_PURCHASE_CODE, P.CREATED_AT, M.NAME AS MEMBER_NAME, A.LETTER_OF_PURCHASE_CODE AS PURCHASE_CODE, A.MEMBER_CODE AS APPROVER_CODE, A.APPROVED, A.APPROVED_AT, B.NAME AS APPROVER_NAME, O.NAME AS POSITION_NAME FROM TBL_LETTER_OF_PURCHASE P JOIN TBL_MEMBER M ON P.MEMBER_CODE = M.MEMBER_CODE JOIN TBL_LETTER_OF_PURCHASE_APPROVER A ON P.LETTER_OF_PURCHASE_CODE = A.LETTER_OF_PURCHASE_CODE JOIN TBL_MEMBER B ON A.MEMBER_CODE = B.MEMBER_CODE JOIN TBL_POSITION O ON B.POSITION_CODE = O.POSITION_CODE
P.LETTER_OF_PURCHASE_CODE = #{letterOfPurchaseCode}
ORDER BY A.APPROVED_AT ASC
